import "../styles/PostStyles.css";
import { Avatar } from "@mui/material";
import { useEffect, useState } from "react";
import { colRef, db } from "../firebase";
import EmojiPicker from 'emoji-picker-react';
import {
  addDoc,
  collection,
  CollectionReference,
  doc,
  DocumentReference,
  onSnapshot,
  serverTimestamp,
} from "firebase/firestore";
import { Button } from "@mui/material";
import { deleteDoc } from "firebase/firestore";
import { BsTrash } from "react-icons/bs";
import { AiOutlineSend } from "react-icons/ai";
import Chatapp from "./Chatapp";

const Post = (props) => {
  console.log();
  const [comments, setComments] = useState([]);
  const [comment, setComment] = useState("");
  // let usernamee= props.userName;
  useEffect(() => {
    let unsubscribe;
    if (props.postId) {
      unsubscribe = onSnapshot(
        collection(db, "posts", props.postId, "comments"),
        (snapshot) => {
          let postss = [];
          snapshot.docs.map((doc) => postss.push(doc.data()));
          setComments(postss);
          console.log(postss);
        }
      );
    }
    return () => {
      unsubscribe();
    };
  }, [props.postId]);

  const deletePost = async (e) => {
    e.preventDefault();
    deleteDoc(await deleteDoc(doc(db, "posts", props.postId)));
  };

  const postComment = async (e) => {
    e.preventDefault();
    await addDoc(collection(db, "posts", props.postId, "comments"), {
      text: comment,
      username: props.userR.displayName,
      // timestamp: serverTimestamp()
    });
    setComment("");
  };
  console.log(comments[0]);
  // console.log(props.userR.displayName);
  return (
    <div className="row">
    <div className="post rounded col-6">
      <div className="post_header row">
        <div className="d-flex col-10 ">
          <Avatar className="post_avatar " alr="Avatar" src="data:image/jpeg;base64," />
          <h4 className="px-3 pt-1">{props.userName}</h4>
        </div>
        <div className="col-2">
          <Button
            className="post_button outline-none"
            type="submit"
            onClick={deletePost}
          >
            <BsTrash size={25} />
          </Button>
        </div>
      </div>
      {/* header -> avatar + username*/}
      <img className="post_image" src={props.imageUrl} alt="alternate image" />
      {/* image */}

      <h4 className="post_text">
        <strong>{props.userName} </strong>
        {props.captionText}
      </h4>
      {/* username + caption  */}

      <div className="post_comments px-4">
        {comments.map((commentt) => (
          <p>
            <b>
              <strong>{commentt.username}</strong>{" "}
            </b>
            {commentt.text}
          </p>
        ))}
      </div>
      {props.userR && (
        <form className="post_commentBox">
          <input
            className="post_input form-control"
            type="text"
            placeholder="add a comment..."
            value={comment}
            onChange={(e) => setComment(e.target.value)}
          />
          {/* <EmojiPicker/> */}
          <Button
            className="post_button"
            disabled={!comment}
            type="submit"
            onClick={postComment}
          >
            <AiOutlineSend size={30} />
          </Button>
        </form>
      )}
    </div>
    <div className="col-6">
    <Chatapp userR={props.userR}/>
    </div>
</div>
  );
};

export default Post;
